SELECT MAX("IMG_REL"."PRED_CLASS_ID" + "IMG_OBJ"."IMG_ID") as agg_0 FROM "IMG_OBJ" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID"  WHERE "IMG_REL"."PRED_CLASS_ID" >= 436 AND "IMG_REL"."OBJ2_SAMPLE_ID" >= 12 AND ("IMG_OBJ"."W" BETWEEN 16 AND 95 OR "IMG_OBJ"."W" >= 1114)