SELECT MAX("IMG_REL"."PRED_CLASS_ID") as agg_0 FROM "IMG_REL" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID"  WHERE "IMG_OBJ"."Y" BETWEEN 159 AND 238 AND ("IMG_REL"."PRED_CLASS_ID" BETWEEN 145 AND 146 OR ("IMG_REL"."PRED_CLASS_ID" BETWEEN 98 AND 147 AND "IMG_REL"."PRED_CLASS_ID" BETWEEN 142 AND 373)) AND "IMG_OBJ"."OBJ_SAMPLE_ID" <= 102