SELECT SUM("IMG_OBJ"."IMG_ID" + "IMG_OBJ"."X") as agg_0, COUNT(*) as agg_1, MIN("IMG_OBJ_ATT"."OBJ_SAMPLE_ID" + "IMG_OBJ"."H") as agg_2 FROM "IMG_OBJ" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID"  WHERE "IMG_OBJ_ATT"."IMG_ID" >= 7155267 AND "IMG_REL"."PRED_CLASS_ID" <= 291 AND "OBJ_CLASSES"."OBJ_CLASS_ID" <= 834