SELECT MAX("IMG_OBJ"."W") as agg_0, SUM("IMG_REL"."OBJ1_SAMPLE_ID") as agg_1, SUM("IMG_REL"."OBJ2_SAMPLE_ID" + "IMG_REL"."IMG_ID") as agg_2 FROM "IMG_REL" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "PRED_CLASSES" ON "IMG_REL"."PRED_CLASS_ID" = "PRED_CLASSES"."PRED_CLASS_ID"  WHERE "PRED_CLASSES"."PRED_CLASS" LIKE '%ag%ainst%' AND "IMG_REL"."OBJ2_SAMPLE_ID" BETWEEN 9 AND 116 AND "PRED_CLASSES"."PRED_CLASS" LIKE '%playing%' AND "IMG_OBJ"."W" BETWEEN 319 AND 1105