SELECT MIN("IMG_OBJ_ATT"."ATT_CLASS_ID") as agg_0, COUNT(*) as agg_1 FROM "PRED_CLASSES" LEFT OUTER JOIN "IMG_REL" ON "PRED_CLASSES"."PRED_CLASS_ID" = "IMG_REL"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID"  WHERE "PRED_CLASSES"."PRED_CLASS" LIKE '%playing%' AND "IMG_OBJ"."OBJ_CLASS_ID" BETWEEN 269 AND 582