SELECT SUM("IMG_OBJ"."X") as agg_0, SUM("IMG_REL"."OBJ1_SAMPLE_ID") as agg_1 FROM "OBJ_CLASSES" LEFT OUTER JOIN "IMG_OBJ" ON "OBJ_CLASSES"."OBJ_CLASS_ID" = "IMG_OBJ"."OBJ_CLASS_ID" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID"  WHERE "OBJ_CLASSES"."OBJ_CLASS_ID" <= 267 AND ("IMG_OBJ"."H" >= 18 OR ("IMG_OBJ"."H" >= 430 AND "IMG_OBJ"."H" BETWEEN -103 AND 21)) AND "OBJ_CLASSES"."OBJ_CLASS" = 'body'