SELECT MAX("IMG_OBJ"."H") as agg_0, COUNT(*) as agg_1 FROM "IMG_REL" LEFT OUTER JOIN "PRED_CLASSES" ON "IMG_REL"."PRED_CLASS_ID" = "PRED_CLASSES"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID"  WHERE "IMG_OBJ"."X" BETWEEN 42 AND 421 AND ("PRED_CLASSES"."PRED_CLASS" != 'sitting on' OR ("PRED_CLASSES"."PRED_CLASS" IN ('against', 'made of', 'from', 'looking at', 'painted on', 'playing on', 'to') AND "PRED_CLASSES"."PRED_CLASS" != 'casting')) AND "IMG_OBJ_ATT"."IMG_ID" >= 5907650