SELECT MAX("IMG_OBJ"."H" + "PRED_CLASSES"."PRED_CLASS_ID") as agg_0, MAX("IMG_OBJ_ATT"."IMG_ID" + "IMG_OBJ"."Y") as agg_1, COUNT(*) as agg_2 FROM "IMG_REL" LEFT OUTER JOIN "PRED_CLASSES" ON "IMG_REL"."PRED_CLASS_ID" = "PRED_CLASSES"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID"  WHERE "IMG_OBJ_ATT"."OBJ_SAMPLE_ID" <= 3 AND "PRED_CLASSES"."PRED_CLASS_ID" BETWEEN 116 AND 231 AND ("PRED_CLASSES"."PRED_CLASS" IN ('made of', 'painted on') OR "PRED_CLASSES"."PRED_CLASS" != 'throwing') AND ("PRED_CLASSES"."PRED_CLASS" LIKE '%of%' OR "PRED_CLASSES"."PRED_CLASS" LIKE '%o%n%') AND ("IMG_OBJ_ATT"."ATT_CLASS_ID" <= 1316 OR "IMG_OBJ_ATT"."ATT_CLASS_ID" >= 871 OR "IMG_OBJ_ATT"."ATT_CLASS_ID" <= 674)