SELECT MIN("IMG_OBJ"."OBJ_CLASS_ID") as agg_0, MIN("IMG_REL"."OBJ2_SAMPLE_ID") as agg_1, AVG("ATT_CLASSES"."ATT_CLASS_ID") as agg_2 FROM "ATT_CLASSES" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "ATT_CLASSES"."ATT_CLASS_ID" = "IMG_OBJ_ATT"."ATT_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID"  WHERE "IMG_REL"."IMG_ID" >= 4768060 AND "IMG_OBJ_ATT"."ATT_CLASS_ID" >= 694 AND "IMG_OBJ"."H" BETWEEN 222 AND 578 AND "IMG_REL"."OBJ1_SAMPLE_ID" >= 7 AND "IMG_OBJ"."OBJ_SAMPLE_ID" >= 20 AND "ATT_CLASSES"."ATT_CLASS" IN ('upside down', 'oval', 'vertical', 'evergreen', 'airborne', 'outdoors')