SELECT SUM("OBJ_CLASSES"."OBJ_CLASS_ID" + "ATT_CLASSES"."ATT_CLASS_ID") as agg_0, SUM("IMG_OBJ"."IMG_ID" + "IMG_OBJ"."W") as agg_1, AVG("ATT_CLASSES"."ATT_CLASS_ID" + "IMG_OBJ"."Y") as agg_2 FROM "ATT_CLASSES" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "ATT_CLASSES"."ATT_CLASS_ID" = "IMG_OBJ_ATT"."ATT_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID"  WHERE ("IMG_OBJ_ATT"."OBJ_SAMPLE_ID" <= 16 OR "IMG_OBJ_ATT"."ATT_CLASS_ID" <= 1331) AND "OBJ_CLASSES"."OBJ_CLASS_ID" BETWEEN 26 AND 759 AND "IMG_OBJ"."H" >= 198 AND ("IMG_OBJ_ATT"."IMG_ID" <= 7156395 OR "IMG_OBJ_ATT"."IMG_ID" >= 6526269) AND "IMG_OBJ"."IMG_ID" BETWEEN 6308983 AND 6364448 AND "OBJ_CLASSES"."OBJ_CLASS" IN ('reflection')