SELECT SUM("IMG_OBJ"."H") as agg_0, MIN("IMG_OBJ"."H") as agg_1, MIN("IMG_REL"."OBJ2_SAMPLE_ID" + "IMG_REL"."PRED_CLASS_ID") as agg_2 FROM "IMG_OBJ_ATT" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID"  WHERE ("IMG_REL"."OBJ1_SAMPLE_ID" >= 6 OR "IMG_REL"."OBJ1_SAMPLE_ID" BETWEEN 11 AND 14 OR "IMG_REL"."OBJ1_SAMPLE_ID" <= 58) AND "IMG_OBJ_ATT"."IMG_ID" <= 2378709 AND "ATT_CLASSES"."ATT_CLASS" LIKE '%and%' AND ("IMG_OBJ"."H" <= 26 OR "IMG_OBJ"."IMG_ID" BETWEEN 4781194 AND 5992633)