SELECT COUNT(*) as agg_0, MAX("IMG_REL"."IMG_ID") as agg_1 FROM "PRED_CLASSES" LEFT OUTER JOIN "IMG_REL" ON "PRED_CLASSES"."PRED_CLASS_ID" = "IMG_REL"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID"  WHERE ("PRED_CLASSES"."PRED_CLASS" LIKE '%laying%' OR ("PRED_CLASSES"."PRED_CLASS_ID" BETWEEN 79 AND 275 AND "PRED_CLASSES"."PRED_CLASS" IN ('belonging to', 'against', 'parked on'))) AND ("OBJ_CLASSES"."OBJ_CLASS" != 'clouds' OR "OBJ_CLASSES"."OBJ_CLASS_ID" >= 37)