SELECT COUNT(*) as agg_0, MAX("IMG_OBJ"."W") as agg_1, SUM("IMG_REL"."IMG_ID" + "IMG_REL"."OBJ2_SAMPLE_ID") as agg_2 FROM "PRED_CLASSES" LEFT OUTER JOIN "IMG_REL" ON "PRED_CLASSES"."PRED_CLASS_ID" = "IMG_REL"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID"  WHERE "PRED_CLASSES"."PRED_CLASS" LIKE '%of%'