SELECT SUM("IMG_OBJ_ATT"."ATT_CLASS_ID" + "IMG_OBJ"."X") as agg_0, SUM("IMG_OBJ"."X" + "IMG_OBJ"."Y") as agg_1 FROM "IMG_OBJ_ATT" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID"  WHERE "IMG_OBJ"."Y" <= 212 AND "IMG_OBJ_ATT"."IMG_ID" >= 2388350 AND "IMG_OBJ_ATT"."OBJ_SAMPLE_ID" >= 2 AND "IMG_OBJ"."OBJ_SAMPLE_ID" >= 3 AND ("IMG_OBJ"."H" <= 37 OR "IMG_OBJ"."IMG_ID" <= 7180025 OR "IMG_OBJ"."H" >= 47) AND "IMG_OBJ_ATT"."ATT_CLASS_ID" >= 852