SELECT MAX("OBJ_CLASSES"."OBJ_CLASS_ID" + "IMG_OBJ"."H") as agg_0, MIN("IMG_OBJ"."IMG_ID" + "PRED_CLASSES"."PRED_CLASS_ID") as agg_1, SUM("OBJ_CLASSES"."OBJ_CLASS_ID") as agg_2 FROM "PRED_CLASSES" LEFT OUTER JOIN "IMG_REL" ON "PRED_CLASSES"."PRED_CLASS_ID" = "IMG_REL"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID"  WHERE ("PRED_CLASSES"."PRED_CLASS" LIKE '%a%t%' OR "PRED_CLASSES"."PRED_CLASS" IN ('made of', 'against', 'from', 'playing on')) AND "OBJ_CLASSES"."OBJ_CLASS" IN ('shelf', 'neck', 'edge', 'trunk', 'headlight') AND "PRED_CLASSES"."PRED_CLASS" IN ('mounted on', 'from', 'made of', 'painted on', 'against', 'parked on', 'playing on', 'belonging to', 'to') AND "OBJ_CLASSES"."OBJ_CLASS_ID" <= 497