SELECT COUNT(*) as agg_0 FROM "PRED_CLASSES" LEFT OUTER JOIN "IMG_REL" ON "PRED_CLASSES"."PRED_CLASS_ID" = "IMG_REL"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID"  WHERE "IMG_REL"."OBJ2_SAMPLE_ID" BETWEEN 2 AND 17 AND ("PRED_CLASSES"."PRED_CLASS_ID" BETWEEN 81 AND 112 OR "PRED_CLASSES"."PRED_CLASS_ID" <= 425 OR "PRED_CLASSES"."PRED_CLASS_ID" >= 394)