SELECT MIN("ATT_CLASSES"."ATT_CLASS_ID") as agg_0, MIN("IMG_OBJ"."IMG_ID") as agg_1 FROM "IMG_REL" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID"  WHERE "IMG_OBJ_ATT"."OBJ_SAMPLE_ID" <= 13 AND "IMG_OBJ_ATT"."IMG_ID" <= 1602343 AND "ATT_CLASSES"."ATT_CLASS_ID" <= 752 AND ("IMG_REL"."PRED_CLASS_ID" <= 296 OR "IMG_REL"."IMG_ID" >= 4774381)