SELECT MIN("IMG_OBJ_ATT"."OBJ_SAMPLE_ID" + "IMG_OBJ_ATT"."IMG_ID") as agg_0, MIN("ATT_CLASSES"."ATT_CLASS_ID") as agg_1 FROM "ATT_CLASSES" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "ATT_CLASSES"."ATT_CLASS_ID" = "IMG_OBJ_ATT"."ATT_CLASS_ID"  WHERE "ATT_CLASSES"."ATT_CLASS_ID" >= 671 AND ("IMG_OBJ_ATT"."IMG_ID" <= 2408107 OR ("IMG_OBJ_ATT"."IMG_ID" BETWEEN 773390 AND 7023569 AND "IMG_OBJ_ATT"."IMG_ID" BETWEEN 2345144 AND 2366751))