SELECT MAX("PRED_CLASSES"."PRED_CLASS_ID") as agg_0, COUNT(*) as agg_1, MAX("PRED_CLASSES"."PRED_CLASS_ID") as agg_2 FROM "PRED_CLASSES"  WHERE "PRED_CLASSES"."PRED_CLASS" NOT LIKE '%ha%s%' AND "PRED_CLASSES"."PRED_CLASS" IN ('painted on', 'belonging to', 'from') AND "PRED_CLASSES"."PRED_CLASS_ID" >= 167