SELECT SUM("IMG_REL"."IMG_ID") as agg_0, AVG("IMG_REL"."IMG_ID") as agg_1 FROM "IMG_OBJ_ATT" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID" LEFT OUTER JOIN "PRED_CLASSES" ON "IMG_REL"."PRED_CLASS_ID" = "PRED_CLASSES"."PRED_CLASS_ID"  WHERE ("IMG_OBJ"."X" >= 7 OR "IMG_OBJ"."IMG_ID" <= 4784049 OR "IMG_OBJ"."X" BETWEEN 138 AND 393) AND "IMG_OBJ"."OBJ_SAMPLE_ID" <= 18 AND "IMG_OBJ_ATT"."ATT_CLASS_ID" BETWEEN 1588 AND 2020 AND "ATT_CLASSES"."ATT_CLASS_ID" <= 1037 AND "ATT_CLASSES"."ATT_CLASS" IN ('building s', 'seated', 'evergreen', 'outdoors', 'rusted')