SELECT MIN("IMG_OBJ_ATT"."OBJ_SAMPLE_ID" + "IMG_OBJ"."IMG_ID") as agg_0 FROM "IMG_OBJ" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID"  WHERE "IMG_OBJ"."Y" <= 257 AND ("OBJ_CLASSES"."OBJ_CLASS" IN ('train', 'edge', 'trunk', 'headlight', 'reflection', 'awning') OR ("OBJ_CLASSES"."OBJ_CLASS" != 'airplane' AND "OBJ_CLASSES"."OBJ_CLASS" != 'bat')) AND "IMG_OBJ"."H" BETWEEN 13 AND 21 AND "IMG_OBJ"."OBJ_SAMPLE_ID" BETWEEN 3 AND 9 AND "OBJ_CLASSES"."OBJ_CLASS_ID" BETWEEN 251 AND 270