SELECT COUNT(*) as agg_0 FROM "IMG_REL" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID"  WHERE "IMG_OBJ_ATT"."IMG_ID" BETWEEN 2366023 AND 3152245 AND "ATT_CLASSES"."ATT_CLASS" NOT LIKE '%blu%e%' AND "IMG_OBJ"."X" BETWEEN 17 AND 465 AND "ATT_CLASSES"."ATT_CLASS" != 'side' AND "IMG_OBJ"."OBJ_CLASS_ID" <= 258 AND "IMG_OBJ"."H" <= -66