SELECT COUNT(*) as agg_0, COUNT(*) as agg_1, SUM("IMG_OBJ"."Y" + "IMG_OBJ_ATT"."ATT_CLASS_ID") as agg_2 FROM "IMG_REL" LEFT OUTER JOIN "PRED_CLASSES" ON "IMG_REL"."PRED_CLASS_ID" = "PRED_CLASSES"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID"  WHERE "PRED_CLASSES"."PRED_CLASS_ID" <= 439 AND "IMG_REL"."OBJ2_SAMPLE_ID" <= 2 AND "IMG_OBJ"."OBJ_SAMPLE_ID" >= 1