SELECT SUM("IMG_OBJ_ATT"."ATT_CLASS_ID" + "IMG_OBJ_ATT"."OBJ_SAMPLE_ID") as agg_0, AVG("ATT_CLASSES"."ATT_CLASS_ID") as agg_1 FROM "ATT_CLASSES" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "ATT_CLASSES"."ATT_CLASS_ID" = "IMG_OBJ_ATT"."ATT_CLASS_ID"  WHERE ("IMG_OBJ_ATT"."OBJ_SAMPLE_ID" BETWEEN 5 AND 11 OR ("IMG_OBJ_ATT"."OBJ_SAMPLE_ID" >= 5 AND "IMG_OBJ_ATT"."OBJ_SAMPLE_ID" >= 1)) AND "ATT_CLASSES"."ATT_CLASS" = 'cut'