SELECT MIN("IMG_OBJ"."W") as agg_0, COUNT(*) as agg_1 FROM "IMG_REL" LEFT OUTER JOIN "PRED_CLASSES" ON "IMG_REL"."PRED_CLASS_ID" = "PRED_CLASSES"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID"  WHERE "IMG_OBJ_ATT"."IMG_ID" <= 8577 AND "IMG_REL"."IMG_ID" >= 5351418 AND "IMG_REL"."OBJ1_SAMPLE_ID" >= 13