SELECT COUNT(*) as agg_0, MIN("IMG_OBJ"."X" + "IMG_OBJ"."IMG_ID") as agg_1 FROM "IMG_OBJ_ATT" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID"  WHERE "IMG_OBJ_ATT"."OBJ_SAMPLE_ID" <= 5 AND "IMG_OBJ_ATT"."IMG_ID" <= 2343999 AND "IMG_OBJ_ATT"."ATT_CLASS_ID" >= 295 AND "IMG_OBJ"."X" >= 332