SELECT SUM("IMG_REL"."IMG_ID" + "IMG_REL"."PRED_CLASS_ID") as agg_0 FROM "IMG_REL" LEFT OUTER JOIN "PRED_CLASSES" ON "IMG_REL"."PRED_CLASS_ID" = "PRED_CLASSES"."PRED_CLASS_ID"  WHERE "IMG_REL"."IMG_ID" >= 2379030 AND "PRED_CLASSES"."PRED_CLASS_ID" <= 387 AND "IMG_REL"."OBJ1_SAMPLE_ID" BETWEEN 2 AND 10 AND "IMG_REL"."PRED_CLASS_ID" BETWEEN 149 AND 297 AND "PRED_CLASSES"."PRED_CLASS" IN ('made of', 'against', 'parked on', 'painted on', 'looking at', 'from', 'playing on', 'belonging to', 'to')