SELECT MAX("PRED_CLASSES"."PRED_CLASS_ID" + "IMG_REL"."OBJ1_SAMPLE_ID") as agg_0, AVG("IMG_OBJ"."X" + "IMG_OBJ_ATT"."ATT_CLASS_ID") as agg_1, MIN("IMG_OBJ"."OBJ_CLASS_ID" + "IMG_REL"."PRED_CLASS_ID") as agg_2 FROM "IMG_REL" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID" LEFT OUTER JOIN "PRED_CLASSES" ON "IMG_REL"."PRED_CLASS_ID" = "PRED_CLASSES"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID"  WHERE "IMG_OBJ"."IMG_ID" BETWEEN 2397945 AND 7244663 AND "OBJ_CLASSES"."OBJ_CLASS_ID" >= 439 AND "PRED_CLASSES"."PRED_CLASS_ID" >= 91 AND "IMG_OBJ"."X" <= 89 AND "IMG_OBJ_ATT"."OBJ_SAMPLE_ID" BETWEEN 4 AND 9