SELECT MAX("IMG_REL"."IMG_ID") as agg_0, SUM("IMG_OBJ_ATT"."ATT_CLASS_ID" + "PRED_CLASSES"."PRED_CLASS_ID") as agg_1 FROM "OBJ_CLASSES" LEFT OUTER JOIN "IMG_OBJ" ON "OBJ_CLASSES"."OBJ_CLASS_ID" = "IMG_OBJ"."OBJ_CLASS_ID" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID" LEFT OUTER JOIN "PRED_CLASSES" ON "IMG_REL"."PRED_CLASS_ID" = "PRED_CLASSES"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID"  WHERE ("PRED_CLASSES"."PRED_CLASS" = 'growing on' OR "PRED_CLASSES"."PRED_CLASS" LIKE '%ne%ar%') AND "IMG_OBJ"."X" <= 153 AND "PRED_CLASSES"."PRED_CLASS" IN ('parked on', 'to', 'belonging to', 'made of', 'playing on', 'mounted on') AND "OBJ_CLASSES"."OBJ_CLASS" != 'watch' AND "IMG_OBJ_ATT"."IMG_ID" >= 2373491