
SELECT COUNT(*) as agg_0 FROM "train" LEFT OUTER JOIN "key" ON "train"."store_nbr" = "key"."store_nbr" LEFT OUTER JOIN "station" ON "key"."station_nbr" = "station"."station_nbr"  WHERE "train"."item_nbr" <= 100 AND "key"."station_nbr" = 18 AND "station"."station_nbr" BETWEEN 2 AND 18 AND "train"."units" >= 0;