
SELECT MAX("train"."units") as agg_0 FROM "station" LEFT OUTER JOIN "key" ON "station"."station_nbr" = "key"."station_nbr" LEFT OUTER JOIN "train" ON "key"."store_nbr" = "train"."store_nbr"  WHERE "key"."store_nbr" BETWEEN 25 AND 29 AND "train"."date" IN ('2012-06-04', '2012-08-29', '2012-12-26', '2012-07-16', '2013-02-15', '2013-02-26', '2012-10-05') AND "train"."store_nbr" BETWEEN 4 AND 37;