
SELECT MAX("station"."station_nbr") as agg_0 FROM "key" LEFT OUTER JOIN "station" ON "key"."station_nbr" = "station"."station_nbr" LEFT OUTER JOIN "train" ON "key"."store_nbr" = "train"."store_nbr"  WHERE "station"."station_nbr" BETWEEN 1 AND 15 AND "key"."station_nbr" >= 8 AND "train"."store_nbr" IS NOT NULL;