
SELECT MAX("train"."item_nbr") as agg_0 FROM "station" LEFT OUTER JOIN "key" ON "station"."station_nbr" = "key"."station_nbr" LEFT OUTER JOIN "train" ON "key"."store_nbr" = "train"."store_nbr"  WHERE ("key"."station_nbr" BETWEEN 17 AND 19 OR "key"."station_nbr" >= 1) AND "train"."units" <= 0 AND "key"."store_nbr" = 9;