
SELECT AVG("station"."station_nbr") as agg_0 FROM "train" LEFT OUTER JOIN "key" ON "train"."store_nbr" = "key"."store_nbr" LEFT OUTER JOIN "station" ON "key"."station_nbr" = "station"."station_nbr"  WHERE ("station"."station_nbr" <= 10 OR "station"."station_nbr" <= 9 OR "station"."station_nbr" <= 5) AND "train"."item_nbr" <= 42 AND "train"."date" != '%2014-06-14%';