
SELECT SUM("train"."units") as agg_0 FROM "train" LEFT OUTER JOIN "key" ON "train"."store_nbr" = "key"."store_nbr" LEFT OUTER JOIN "station" ON "key"."station_nbr" = "station"."station_nbr"  WHERE "train"."item_nbr" BETWEEN 46 AND 47 AND ("train"."date" IN ('2012-12-26', '2013-02-26', '2013-02-21', '2012-10-05', '2012-07-16', '2012-07-27', '2012-09-06') OR ("train"."store_nbr" != 16 AND "train"."date" IN ('2013-02-21', '2012-07-27', '2012-07-16', '2012-12-26', '2012-10-05', '2013-02-15', '2013-02-26'))) AND "station"."station_nbr" <= 14 AND ("key"."station_nbr" BETWEEN 2 AND 9 OR ("key"."store_nbr" BETWEEN 6 AND 39 AND "key"."station_nbr" >= 19));